site.headStyle:
nav {
  font-family: "PT Sans", sans-serif;
  font-size: 23px;
  font-weight: bold;
  color: #586265;
  margin: 5px;
}

h4 {
  font-size: 20px;
  font-weight: bold;
  margin: 10px;
  color: #000;
}

.button {
  border-radius: 50px;
  background-color: #86b499;
  color: white;
  font-size: 20px;
  font-weight: bold;
  text-transform: uppercase;
  padding: 20px 60px;
  border: none;
  cursor: pointer;
  transition: background-color 0.3s ease;
}

.button:hover {
  background-color: #6a8f76;
}

.small-button {
  border-radius: 50px;
  background-color: #798c97;
  color: white;
  font-size: 12px;
  font-weight: bold;
  text-transform: uppercase;
  padding: 15px 50px;
  border: none;
  cursor: pointer;
  transition: background-color 0.3s ease;
}

.small-button:hover {
  background-color: #0a1113;
}

.dropdown {
  position: relative;
  display: inline-block;
  z-index: 1; /* Ensure the dropdown is on top */
}

.dropdown-content {
  display: none;
  position: absolute;
  background-color: #f9f9f9;
  min-width: 160px;
  box-shadow: 0px 8px 16px 0px rgba(0, 0, 0, 0.2);
  z-index: 9999; /* Ensure the dropdown is on top */
  animation: dropdown-animation 0.3s ease; /* Add animation */
  padding: 10px; /* Add padding within the dropdown */
}

.dropdown:hover .dropdown-content {
  display: block;
}

.dropdown-content a {
  display: block;
  padding: 6px 0; /* Add spacing between links */
  font-weight: normal;
}

/* Animation keyframes */
@keyframes dropdown-animation {
  from {
    opacity: 0;
    transform: translateY(-10px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}
